def read_data():    data=open('day13.txt').readlines()    coord = []    f = []    for l in data:        l=l.strip()        if ',' in l:            x,y=l.split(',')            coord.append((int(x),int(y)))        elif l:            c,i=l.split('=')            f.append((c[-1],int(i)))    return(coord,f)                def fold(coord,f):    nc = []    d,i=f.pop(0)    if d=='y':        for x,y in coord:            if y in range(i):                if (x,y) not in nc:                    nc.append((x,y))            else:                if 2*i-y in range(i):                    if (x,2*i-y) not in nc:                        nc.append((x,2*i-y))        return(nc,f)    else:        for x,y in coord:            if x in range(i):                if (x,y) not in nc:                    nc.append((x,y))            else:                if 2*i-x in range(i):                    if (2*i-x,y) not in nc:                        nc.append((2*i-x,y))        return(nc,f)        print('Part1 :',len(fold(read_data()[0],read_data()[1])[0]))def full_fold(coord,f):    while f:        coord,f=fold(coord,f)    return(coord)def limits(coord):    x1,y1=0,0    for x,y in coord:        if x>x1:            x1=x        if y>y1:            y1=y    return(x1,y1)def part2():    a,b=read_data()    coord=full_fold(a,b)    x1,y1=limits(coord)    for i in range(y1+1):        c=''        for j in range(x1+1):            if (j,i) in coord:                c+='#'            else:                c+='.'        print(c)    return()part2()