data = sorted([int(i) for i in open('day7.txt').readlines()[0].split(',')])def part1():    m = int((data[(len(data)-1)/2] + data[(len(data)+1)/2] ) / 2) if len(data)%2==1 else data[int((len(data)-1)/2)]    c = 0    for e in data:        c+=abs(m-e)    print("Part 1: ",c)    return()part1()def part2():    m = round(sum(data)/len(data)) #closest integer to mean.    can = []    for i in range(m-2,m+3):        c = 0        for e in data:            n = abs(e-i)            c+= int(n*(n+1)/2)        can.append(c)    print("Part2 :",min(can))    return()part2()